// Helper functie om de Tailwind-kleurklasse op basis van type te krijgen
const getElementColorClass = (type) => {
    const colorMap = {
        'niet-metaal': 'bg-blue-400',
        'edelgas': 'bg-purple-400',
        'alkalimetaal': 'bg-red-400',
        'aardalkalimetaal': 'bg-orange-400',
